                        $nameImage = uniqid() . '.jpg';
                        $thumbnail = $pathFolder . $nameImage;
                        if (file_put_contents($thumbnail, $imageContent)) {
                            $data = array_merge(
                                $article,
                                [
                                    'id_user' => $ids[array_rand($ids)], // random id hoặc null
                                    'thumbnail' => 'storage/image/thumbnail/articles/' . $nameImage,
                                    'search_number' => random_int(0, 300),
                                    'is_accept' => 1,
                                    'is_show' => 1,
                                    'created_at' => now(),
                                    'updated_at' => now(),
                                ]
                            );
                            Article::create($data);
                            break;
                        }
                    }
                } catch (\Exception $e) {
                }
            }
        } catch (\Exception $e) {
        }
    }
}
